--[[START lamp_button.lua]]local a=self.children.lamp;local b=self.children.midi;function onValueChanged(c)if c=='touch'and self.values.touch then if a.values.x<0.5 then b.values.x=1;a:setValueField('x',ValueField.DEFAULT,1)else b.values.x=0;a:setValueField('x',ValueField.DEFAULT,0)end end end--[[END lamp_button.lua]]