--[[START update_midi_channel.lua]]local a=root:findByName('obxdCtrls',true)local b=self.parent.parent.parent;local c={}local d=1;function controlEligible(e)if#e.messages.MIDI>0 then return true end;return false end;function getAllControls(e)if e==nil then print('ERROR')return end;for i=1,#e.children do getAllControls(e.children[i])end;if controlEligible(e)then table.insert(c,e)end end;function updateChannelTags(e)getAllControls(e)for f,g in pairs(c)do e.tag=d end end;function init()updateChannelTags(a)updateChannelTags(b)end;function onReceiveNotify(e)if e=='up'then d=math.min(15,d+1)elseif e=='down'then d=math.max(0,d-1)end;self.values.text=d+1 end;function onValueChanged(f)c={}if f=='text'then i=tonumber(self.values.text)if i~=nil then d=math.min(15,math.max(0,i-1))end;self.values.text=d+1;updateChannelTags(a)end end--[[END update_midi_channel.lua]]